
/**
 * Count the number of bookmarkers.
 * Doesn't really matter if we choose redis or mongodb here.
 */

var redis = require('../databases/redis')

exports.bookmarkers = function (thing, type) {
  var key = 'bookmarkee:' + type + ':' + thing._id.toHexString()
  return redis.zcard(key)
}

exports.bookmarks = function (user, collection, type) {
  var key = 'bookmarker:' + type + ':' + collection + ':' + user._id.toHexString()
  return redis.zcard(key)
}
