
var assert = require('http-assert')

var Thing = require('../things/thing')
var types = require('./types.json')

module.exports = Report

function Report() {
  Thing.call(this)

  this.collection = 'reports'
}

Thing.extend(Report)

Report.set.type = function (report, type) {
  assert(type && ~types.indexOf(type), 422, 'Invalid report type.')
  report.type = type
}
