
/**
 * Common indexes to all things
 */

var mdb = require('../databases/mongodb')

exports.name = function (collection) {
  return mdb.collection(collection).ensureIndex({
    name: 1
  }, {
    sparse: true,
    unique: true,
    background: true
  })
}

exports.slug = function (collection) {
  return mdb.collection(collection).ensureIndex({
    name: 1
  }, {
    sparse: true,
    unique: true,
    background: true
  })
}
