
var mdb = require('../databases/mongodb')

var indexes = module.exports = []

// for tallying
indexes.push(mdb.collection('votes').ensureIndex({
  'thing._id': 1,
  type: 1,
  'date.created': -1
}, {
  background: true
}))

// for job history
indexes.push(mdb.collection('votes.jobs').ensureIndex({
  'input.thing._id': 1,
  'ended': -1
}, {
  background: true
}))
